// ===== LogStreaming Wrapper =====

struct mavsdk_log_streaming_wrapper {
    std::shared_ptr<mavsdk::LogStreaming> cpp_plugin;
    std::mutex handles_mutex;
    std::vector<mavsdk::LogStreaming::LogStreamingRawHandle*> log_streaming_raw_handles;
};

mavsdk_log_streaming_t
mavsdk_log_streaming_create(mavsdk_system_t system) {
    if (system == nullptr) {
        return nullptr;
    }

    auto wrapper = new mavsdk_log_streaming_wrapper();
    auto system_ptr = reinterpret_cast<std::shared_ptr<mavsdk::System>*>(system);
    wrapper->cpp_plugin = std::make_shared<mavsdk::LogStreaming>(*system_ptr);

    return reinterpret_cast<mavsdk_log_streaming_t>(wrapper);
}

void mavsdk_log_streaming_destroy(mavsdk_log_streaming_t log_streaming) {
    if (log_streaming == nullptr) {
        return;
    }

    auto wrapper = reinterpret_cast<mavsdk_log_streaming_wrapper*>(log_streaming);

    // Unsubscribe all active streams before destroying to prevent
    // callbacks firing into a destroyed object
    {
        std::lock_guard<std::mutex> lock(wrapper->handles_mutex);
        for (auto* h : wrapper->log_streaming_raw_handles) {
            wrapper->cpp_plugin->unsubscribe_log_streaming_raw(std::move(*h));
            delete h;
        }
        wrapper->log_streaming_raw_handles.clear();
    }

    delete wrapper;
}

