// ===== C++ to C Type Conversions =====

static mavsdk_manual_control_result_t
translate_result(mavsdk::ManualControl::Result cpp_result) {
    switch(cpp_result) {
        case mavsdk::ManualControl::Result::Unknown:
            return MAVSDK_MANUAL_CONTROL_RESULT_UNKNOWN;
        case mavsdk::ManualControl::Result::Success:
            return MAVSDK_MANUAL_CONTROL_RESULT_SUCCESS;
        case mavsdk::ManualControl::Result::NoSystem:
            return MAVSDK_MANUAL_CONTROL_RESULT_NO_SYSTEM;
        case mavsdk::ManualControl::Result::ConnectionError:
            return MAVSDK_MANUAL_CONTROL_RESULT_CONNECTION_ERROR;
        case mavsdk::ManualControl::Result::Busy:
            return MAVSDK_MANUAL_CONTROL_RESULT_BUSY;
        case mavsdk::ManualControl::Result::CommandDenied:
            return MAVSDK_MANUAL_CONTROL_RESULT_COMMAND_DENIED;
        case mavsdk::ManualControl::Result::Timeout:
            return MAVSDK_MANUAL_CONTROL_RESULT_TIMEOUT;
        case mavsdk::ManualControl::Result::InputOutOfRange:
            return MAVSDK_MANUAL_CONTROL_RESULT_INPUT_OUT_OF_RANGE;
        case mavsdk::ManualControl::Result::InputNotSet:
            return MAVSDK_MANUAL_CONTROL_RESULT_INPUT_NOT_SET;
    }
}




