static mavsdk_offboard_result_t
translate_result(mavsdk::Offboard::Result cpp_result) {
    switch(cpp_result) {
        case mavsdk::Offboard::Result::Unknown:
            return MAVSDK_OFFBOARD_RESULT_UNKNOWN;
        case mavsdk::Offboard::Result::Success:
            return MAVSDK_OFFBOARD_RESULT_SUCCESS;
        case mavsdk::Offboard::Result::NoSystem:
            return MAVSDK_OFFBOARD_RESULT_NO_SYSTEM;
        case mavsdk::Offboard::Result::ConnectionError:
            return MAVSDK_OFFBOARD_RESULT_CONNECTION_ERROR;
        case mavsdk::Offboard::Result::Busy:
            return MAVSDK_OFFBOARD_RESULT_BUSY;
        case mavsdk::Offboard::Result::CommandDenied:
            return MAVSDK_OFFBOARD_RESULT_COMMAND_DENIED;
        case mavsdk::Offboard::Result::Timeout:
            return MAVSDK_OFFBOARD_RESULT_TIMEOUT;
        case mavsdk::Offboard::Result::NoSetpointSet:
            return MAVSDK_OFFBOARD_RESULT_NO_SETPOINT_SET;
        case mavsdk::Offboard::Result::Failed:
            return MAVSDK_OFFBOARD_RESULT_FAILED;
    }
}



