"""
Inject failures into system to test failsafes.
"""

import asyncio
from typing import AsyncGenerator

from pymavsdk.plugins.failure import (
    Failure,
    FailureUnit,
    FailureType,
)


class FailureError(Exception):
    """Raised when a Failure operation fails."""

    def __init__(self, result, origin, *params):
        self._result = result
        self._origin = origin
        self._params = params

    def __str__(self):
        return f"{self._result}: '{self._result.name}'; origin: {self._origin}; params: {self._params}"


class FailureAsync:
    """
    Inject failures into system to test failsafes.

    Async wrapper around :class:`Failure` that mirrors the gRPC-based
    asyncio API while using the ctypes-based C library directly.
    """

    def __init__(self, system):
        self._plugin = Failure(system._system)

    async def inject(self, failure_unit, failure_type, instance):
        """
        Injects a failure.

        Parameters
        ----------
        failure_unit : <protoc_gen_mavsdk.name_parser.NameParser object at 0x108664ad0>
        failure_type : <protoc_gen_mavsdk.name_parser.NameParser object at 0x108664ad0>
        instance : int32_t
        Raises
        ------
        FailureError
            If the request fails. The error contains the reason for the failure.
        """
        loop = asyncio.get_running_loop()
        return await loop.run_in_executor(
            None, lambda: self._plugin.inject(failure_unit, failure_type, instance)
        )
