class GripperError(Exception):
    """Raised when a Gripper operation fails."""

    def __init__(self, result, origin, *params):
        self._result = result
        self._origin = origin
        self._params = params

    def __str__(self):
        return f"{self._result}: '{self._result.name}'; origin: {self._origin}; params: {self._params}"


class GripperAsync:
    """
    Allows users to send gripper actions.

    Async wrapper around :class:`Gripper` that mirrors the gRPC-based
    asyncio API while using the ctypes-based C library directly.
    """

    def __init__(self, system):
        self._subscription_handles: dict = {}
        self._plugin = Gripper(system._system)

    async def grab(self, instance):
        """
        Gripper grab cargo.

        Parameters
        ----------
        instance : uint32_t
        Raises
        ------
        GripperError
            If the request fails. The error contains the reason for the failure.
        """
        loop = asyncio.get_running_loop()
        return await loop.run_in_executor(None, lambda: self._plugin.grab(instance))

    async def release(self, instance):
        """
        Gripper release cargo.

        Parameters
        ----------
        instance : uint32_t
        Raises
        ------
        GripperError
            If the request fails. The error contains the reason for the failure.
        """
        loop = asyncio.get_running_loop()
        return await loop.run_in_executor(None, lambda: self._plugin.release(instance))

    def destroy(self):
        self._subscription_handles.clear()
        self._plugin.destroy()

    def __del__(self):
        self.destroy()
