class LogFilesError(Exception):
    """Raised when a LogFiles operation fails."""

    def __init__(self, result, origin, *params):
        self._result = result
        self._origin = origin
        self._params = params

    def __str__(self):
        return f"{self._result}: '{self._result.name}'; origin: {self._origin}; params: {self._params}"


class LogFilesAsync:
    """
       Allow to download log files from the vehicle after a flight is complete.
    For log streaming during flight check the logging plugin.

       Async wrapper around :class:`LogFiles` that mirrors the gRPC-based
       asyncio API while using the ctypes-based C library directly.
    """

    def __init__(self, system):
        self._subscription_handles: dict = {}
        self._plugin = LogFiles(system._system)

    async def get_entries(self):
        """
        Get List of log files.

        Returns
        -------
        <protoc_gen_mavsdk.name_parser.NameParser object at 0x1064576b0> : Entry
        Raises
        ------
        LogFilesError
            If the request fails. The error contains the reason for the failure.
        """
        loop = asyncio.get_running_loop()
        return await loop.run_in_executor(None, lambda: self._plugin.get_entries())

    async def download_log_file(self, entry, path) -> AsyncGenerator:
        """
        Download log file.
        """
        loop = asyncio.get_running_loop()
        queue: asyncio.Queue = asyncio.Queue()

        def callback(result, data, _user_data=None):
            loop.call_soon_threadsafe(queue.put_nowait, (result, data))

        self._plugin.download_log_file_async(callback)
        while True:
            result, data = await queue.get()
            yield result, data
            if result != LogFilesResult.NEXT:
                break

    async def erase_all_log_files(self):
        """
        Erase all log files.

        Raises
        ------
        LogFilesError
            If the request fails. The error contains the reason for the failure.
        """
        loop = asyncio.get_running_loop()
        return await loop.run_in_executor(
            None, lambda: self._plugin.erase_all_log_files()
        )

    def destroy(self):
        self._subscription_handles.clear()
        self._plugin.destroy()

    def __del__(self):
        self.destroy()
