class LogStreamingError(Exception):
    """Raised when a LogStreaming operation fails."""

    def __init__(self, result, origin, *params):
        self._result = result
        self._origin = origin
        self._params = params

    def __str__(self):
        return f"{self._result}: '{self._result.name}'; origin: {self._origin}; params: {self._params}"


class LogStreamingAsync:
    """
    Provide log streaming data.

    Async wrapper around :class:`LogStreaming` that mirrors the gRPC-based
    asyncio API while using the ctypes-based C library directly.
    """

    def __init__(self, system):
        self._subscription_handles: dict = {}
        self._plugin = LogStreaming(system._system)

    async def start_log_streaming(self):
        """
        Start streaming logging data.

        Raises
        ------
        LogStreamingError
            If the request fails. The error contains the reason for the failure.
        """
        loop = asyncio.get_running_loop()
        return await loop.run_in_executor(
            None, lambda: self._plugin.start_log_streaming()
        )

    async def stop_log_streaming(self):
        """
        Stop streaming logging data.

        Raises
        ------
        LogStreamingError
            If the request fails. The error contains the reason for the failure.
        """
        loop = asyncio.get_running_loop()
        return await loop.run_in_executor(
            None, lambda: self._plugin.stop_log_streaming()
        )

    async def subscribe_log_streaming_raw(
        self,
    ) -> AsyncGenerator[LogStreamingRaw, None]:
        """
        Subscribe to logging messages

        Yields
        ------
        log_streaming_raw : LogStreamingRaw
             The next update
        """
        loop = asyncio.get_running_loop()
        queue: asyncio.Queue = asyncio.Queue()

        def callback(data, _user_data):
            loop.call_soon_threadsafe(queue.put_nowait, data)

        handle = self._plugin.subscribe_log_streaming_raw(callback)
        self._subscription_handles[id(queue)] = handle
        try:
            while True:
                yield await queue.get()
        finally:
            if id(queue) in self._subscription_handles:
                self._subscription_handles.pop(id(queue))
                self._plugin.unsubscribe_log_streaming_raw(handle)

    def destroy(self):
        self._subscription_handles.clear()
        self._plugin.destroy()

    def __del__(self):
        self.destroy()
