"""
Enable direct MAVLink communication using libmav.
"""

import asyncio
from typing import AsyncGenerator
from mavsdk.plugins.mavlink_direct import (
    MavlinkDirect,
    MavlinkDirectResult,
    MavlinkMessage,
)


class MavlinkDirectError(Exception):
    """Raised when a MavlinkDirect operation fails."""

    def __init__(self, result, origin, *params):
        self._result = result
        self._origin = origin
        self._params = params

    def __str__(self):
        return f"{self._result}: '{self._result.name}'; origin: {self._origin}; params: {self._params}"


class MavlinkDirectAsync:
    """
    Enable direct MAVLink communication using libmav.

    Async wrapper around :class:`MavlinkDirect` that mirrors the gRPC-based
    asyncio API while using the ctypes-based C library directly.
    """

    def __init__(self, system):
        self._subscription_handles: dict = {}
        self._plugin = MavlinkDirect(system._system)

    async def send_message(self, message):
        """
               Send a MAVLink message directly to the system.

        This allows sending any MAVLink message with full control over the message content.

               Parameters
               ----------
               message : MavlinkMessage
               Raises
               ------
               MavlinkDirectError
                   If the request fails. The error contains the reason for the failure.
        """
        loop = asyncio.get_running_loop()
        return await loop.run_in_executor(
            None, lambda: self._plugin.send_message(message)
        )

    async def subscribe_message(self) -> AsyncGenerator[MavlinkMessage, None]:
        """
               Subscribe to incoming MAVLink messages.

        This provides direct access to incoming MAVLink messages. Use an empty string
        in message_name to subscribe to all messages, or specify a message name
        (e.g., "HEARTBEAT") to filter for specific message types.

               Yields
               ------
               mavlink_message : MavlinkMessage
                    The next update
        """
        loop = asyncio.get_running_loop()
        queue: asyncio.Queue = asyncio.Queue()

        def callback(data, _user_data):
            loop.call_soon_threadsafe(queue.put_nowait, data)

        handle = self._plugin.subscribe_message(callback)
        self._subscription_handles[id(queue)] = handle
        try:
            while True:
                yield await queue.get()
        finally:
            if id(queue) in self._subscription_handles:
                self._subscription_handles.pop(id(queue))
                self._plugin.unsubscribe_message(handle)

    async def load_custom_xml(self, xml_content):
        """
               Load custom MAVLink message definitions from XML.

        This allows loading custom MAVLink message definitions at runtime,
        extending the available message types beyond the built-in definitions.

               Parameters
               ----------
               xml_content : str
               Raises
               ------
               MavlinkDirectError
                   If the request fails. The error contains the reason for the failure.
        """
        loop = asyncio.get_running_loop()
        return await loop.run_in_executor(
            None, lambda: self._plugin.load_custom_xml(xml_content)
        )

    def destroy(self):
        self._subscription_handles.clear()
        self._plugin.destroy()

    def __del__(self):
        self.destroy()
