# ===== Plugin =====
class ServerUtility:
    """Utility for onboard MAVSDK instances for common "server" tasks."""

    def __init__(self, system):
        self._lib = _cmavsdk_lib
        self._handle = None
        self._callbacks = []  # Keep references to prevent GC

        if system is None:
            raise ValueError("system cannot be None")

        system_handle = system._handle

        if not system_handle:
            raise ValueError("system handle is null")

        self._handle = self._lib.mavsdk_server_utility_create(system_handle)

        if not self._handle:
            raise RuntimeError(
                "Failed to create ServerUtility plugin - C function returned null handle"
            )

        atexit.register(self.destroy)

    def send_status_text(self, type, text):
        """Get send_status_text (blocking)"""

        result_code = self._lib.mavsdk_server_utility_send_status_text(
            self._handle,
            type,
            text.encode("utf-8") if isinstance(text, str) else text,
        )
        result = ServerUtilityResult(result_code)
        if result != ServerUtilityResult.SUCCESS:
            raise Exception(f"send_status_text failed: {result}")

        return result

    def destroy(self):
        """Destroy the plugin instance"""
        if self._handle:
            self._lib.mavsdk_server_utility_destroy(self._handle)
            self._handle = None

    def __del__(self):
        self.destroy()


