# ===== Result Enums =====
class ShellResult(IntEnum):
    """Possible results returned for shell requests"""

    UNKNOWN = 0
    SUCCESS = 1
    NO_SYSTEM = 2
    CONNECTION_ERROR = 3
    NO_RESPONSE = 4
    BUSY = 5


