class ServerUtility:
    """Utility for onboard MAVSDK instances for common "server" tasks."""

    def __init__(self, lib: ctypes.CDLL, system):
        self._lib = lib
        self._handle = None
        self._callbacks = []  # Keep references to prevent GC

        self._setup_functions()

        # Validate input parameter
        if system is None:
            raise ValueError("system cannot be None")

        # Extract handle with validation
        if hasattr(system, '_handle'):
            system_handle = system._handle
        elif isinstance(system, ctypes.c_void_p):
            system_handle = system
        else:
            raise ValueError(f"Invalid system type: {type(system)}. Expected MavsdkSystem or c_void_p")

        # Validate handle is not null
        if not system_handle or (isinstance(system_handle, int) and system_handle == 0):
            raise ValueError("system handle is null")

        self._handle = self._lib.mavsdk_server_utility_create(system_handle)

        # Validate plugin creation succeeded
        if not self._handle:
            raise RuntimeError("Failed to create ServerUtility plugin - C function returned null handle")

    def _setup_functions(self):
        """Setup C function signatures"""

        # Create/Destroy
        self._lib.mavsdk_server_utility_create.argtypes = [ctypes.c_void_p]
        self._lib.mavsdk_server_utility_create.restype = ctypes.c_void_p

        self._lib.mavsdk_server_utility_destroy.argtypes = [ctypes.c_void_p]
        self._lib.mavsdk_server_utility_destroy.restype = None



        self._lib.mavsdk_server_utility_send_status_text.argtypes = [
            ctypes.c_void_p,
            ctypes.c_int,
            ctypes.c_char_p,
        ]

        self._lib.mavsdk_server_utility_send_status_text.restype = ctypes.c_int



    def send_status_text(self, type, text):
        """Get send_status_text (blocking)"""
        result_code = self._lib.mavsdk_server_utility_send_status_text(
            self._handle,
            type,             text        )
        result = ServerUtilityResult(result_code)
        if result != ServerUtilityResult.SUCCESS:
            raise Exception(f"send_status_text failed: {result}")
        return result


    def destroy(self):
        """Destroy the plugin instance"""
        if self._handle:
            self._lib.mavsdk_server_utility_destroy(self._handle)
            self._handle = None

    def __del__(self):
        self.destroy()